public class SumOfTwo {
    public static void main(String[] args) {
        // Declare two integer variables
        int num1 = 10;
        int num2 = 20;

        // Calculate the sum
        int sum = num1 + num2;

        // Print the result
        System.out.println("The sum of " + num1 + " and " + num2 + " is: " + sum);
    }
}

